exports.inject = function () {

	return {
		build: function (validator, mapping, db) {
			var memoizer = {};
			return {
				login: function (obj, cb) {
					
				},
				auth: function (sid, path, cb) {
					
				},
				on: function (type) {
					var h = memoizer[type];
					if (typeof h == undefined) {
						var t = mapping.find(type);
						h = {
							getOne: function(id,cb) {},
							getAll: function(crit,cb) {},
							create: function(obj,cb) {},
							update: function(obj,cb) {},
							delete: function(id,cb) {}
						}
						memoizer[type] = h;
					}
					return h;
				}
			}
		}
	}
}
