/**
 * 
 */
 
exports.build = function (rules) {
	return function (updatable) {
		// run every rules
		for (i = 0, l = rules.length; i < l ; i++) {
			var o = rules[i];
			// if o is a valid rule we run it
			if (o.valid(updatable)) {
				// if no update of the content 
				if (! (updatable = o.run(updatable))) {
					break;
				}
			}
		}
	}
}
