exports.inject = function (mongo, db_name, host, port) {
	host = host || 'localhost';
	port = port || mongo.Connection.DEFAULT_PORT;
	return {
		ask: function (col_name, callback) {
			var db = new mongo.Db(db_name, new mongo.Server(host, port));
			
			db.open(function(err, con) {
				con.createCollection(col_name, function() {
					db.collection(col_name, function(error, collection) {
						if (typeof error!='undefined' && error != null) console.log('ERROR:',error)
						callback(collection)
						db.close()	
					})	
				})	
			})
		}
	}
}
